public class Students {    private int id;    private String name;    private int age;    private String sex;    private String address;    private double scores;    public Students() {    }    public Students(int id, String name, int age, String sex, String address, double scores) {        this.id = id;        this.name = name;        this.age = age;        this.sex = sex;        this.address = address;        this.scores = scores;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    public String getSex() {        return sex;    }    public void setSex(String sex) {        this.sex = sex;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }    public double getScores() {        return scores;    }    public void setScores(double scores) {        this.scores = scores;    }    @Override    public String toString() {        return                "Mã sinh viên: " + id + "\n" +                        "Tên: " + name +                        ", Tuổi: " + age +                        ", Giới tính: " + sex +                        ", Địa chỉ: " + address +                        ", Điểm trung bình: " + scores + "\n";    }}